import React from "react";
import { motion } from "framer-motion";

// Define animation variants
const containerVariants = {
  visible: { opacity: 1, transition: { staggerChildren: 0.5 } },
  hidden: { opacity: 0 },
};

const itemVariants = {
  visible: { opacity: 1, y: 0 },
  hidden: { opacity: 0, y: 20 },
};

const buttonVariants = {
  hover: { scale: 1.05 },
  tap: { scale: 0.95 },
};

const HeroSection = () => {
  return (
    <motion.section
      variants={containerVariants}
      initial="hidden"
      animate="visible"
      className="text-gray-600 body-font pb-8 md:pb-12"
      style={{ marginTop: 0, paddingTop: 0 }} // No margin/padding on top
    >
      <div className="container mx-auto flex px-4 py-0 md:py-4 md:flex-row flex-col items-center" style={{ paddingTop: '0px' }}>
        {/* Text Content */}
        <motion.div
          variants={itemVariants}
          className="lg:flex-grow md:w-1/2 lg:pr-24 md:pr-16 flex flex-col md:items-start md:text-left mb-0 md:mb-0 items-center text-center"
          style={{ marginTop: '0px', paddingTop: '0px' }} // Ensure no top margin/padding
        >
          <motion.h1
            variants={itemVariants}
            className="title-font text-2xl sm:text-3xl md:text-4xl lg:text-5xl mb-4 font-medium text-gray-900"
          >
            Experience the Thrill of Cricket
            <br className="hidden lg:inline-block" />
            with CRICKO Organization
          </motion.h1>
          <motion.p
            variants={itemVariants}
            className="text-base sm:text-lg md:text-xl mb-8 leading-relaxed"
          >
            Join us for exciting cricket tournaments in various formats. Whether you're a player or a fan, we've got something for everyone. Discover the passion, skill, and excitement of cricket with CRICKO Organization.
          </motion.p>
          <div className="flex justify-center space-x-4">
            <motion.button
              variants={buttonVariants}
              whileHover="hover"
              whileTap="tap"
              className="inline-flex text-white bg-custom-blue border-0 py-2 px-6 text-base md:text-lg focus:outline-none hover:bg-custom-blue/80 rounded"
            >
              Explore Tournaments
            </motion.button>
            <motion.button
              variants={buttonVariants}
              whileHover="hover"
              whileTap="tap"
              className="inline-flex text-gray-700 bg-gray-100 border-0 py-2 px-6 text-base md:text-lg focus:outline-none hover:bg-gray-200 rounded"
            >
              Register Team
            </motion.button>
          </div>
        </motion.div>

        {/* Image */}
        <motion.div
          variants={itemVariants}
          className="lg:max-w-lg lg:w-full md:w-1/2 w-full"
          style={{ marginTop: '0px', paddingTop: '0px' }} // Ensure no top margin/padding for image
        >
          <motion.img
            className="object-cover object-center rounded-lg border border-gray-300"
            alt="Cricket Match"
            src="https://media4.giphy.com/media/FeBamTuuPUTnFrqfx0/giphy.gif?cid=6c09b952djjkt4561ci63tffwnza5oomjujkkxuegg10cmyc&ep=v1_stickers_related&rid=giphy.gif&ct=s"
            initial={{ opacity: 0, scale: 0.8 }} // Animation effect for image
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.5 }}
          />
        </motion.div>
      </div>
    </motion.section>
  );
};

export default HeroSection;
