import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const TeamFormPage1 = () => {
  const [formData, setFormData] = useState({
    tname: "",
    taddress: "",
    captname: "",
    phone: "",
    logo: ""
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Store data in localStorage or state management solution
    localStorage.setItem("teamFormData", JSON.stringify(formData));
    navigate("/register/page2");
  };

  return (
    <div className="container mx-auto p-4">
      <div className="max-w-md mx-auto"> {/* Centered the form container and restricted width */}
        <h2 className="text-xl font-bold mb-4 text-center">Register Your Team</h2> {/* Centered the title */}
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700">Team Name</label>
            <input
              type="text"
              name="tname"
              value={formData.tname}
              onChange={handleChange}
              className="mt-1 block w-80 p-2 border border-gray-300 rounded-md mx-auto" // Adjusted width
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">Team Address</label>
            <input
              type="text"
              name="taddress"
              value={formData.taddress}
              onChange={handleChange}
              //className="mt-1 block w-80 p-2 border border-gray-300 rounded-md mx-auto" // Adjusted width
              className="mt-1 block w-full p-1 border border-gray-300 rounded-md text-sm"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">Captain's Name</label>
            <input
              type="text"
              name="captname"
              value={formData.captname}
              onChange={handleChange}
              className="mt-1 block w-80 p-2 border border-gray-300 rounded-md mx-auto" // Adjusted width
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">Phone Number</label>
            <input
              type="text"
              name="phone"
              value={formData.phone}
              onChange={handleChange}
              className="mt-1 block w-80 p-2 border border-gray-300 rounded-md mx-auto" // Adjusted width
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">Team Logo URL</label>
            <input
              type="text"
              name="logo"
              value={formData.logo}
              onChange={handleChange}
              className="mt-1 block w-80 p-2 border border-gray-300 rounded-md mx-auto" // Adjusted width
              //className="bg-blue-500 text-white px-3 py-1 rounded-md hover:bg-blue-600 text-sm"
            />
          </div>

          <button
            type="submit"
            className="bg-blue-500 text-white px-2 py-2 rounded-md hover:bg-blue-600 w-40 mx-auto block" // Adjusted width
          >
            Next
          </button>
        </form>
      </div>
    </div>
  );
};

export default TeamFormPage1;
